    function consumeSnapshot1Balance(address) external returns (uint256);

    function consumeSnapshot2Balance(address) external returns (uint256);
}

interfaces/IProtocolFund.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

uint256 constant REDEEM_INFO_AMOUNT_OFFSET = 208;

uint256 constant REDEEM_INFO_SUPPLY_OFFSET = 160;

/**
 * `RedeemInfo` layout
 * |-- amount --|-- supply --|--  redeemer --|
 * |--   48   --|--   48   --|--    160    --|
 */
type RedeemInfo is uint256;

interface IProtocolFund {
    /**
     * @param redeemInfo the packed struct containing redeemed amount out of the
     *                   total supply and the redeemer address.
     *
     * In the signal chain `ProtocolFund`, the method completes the asset transfer
     * atomically. The remote `ProtocolFund`
     *
     */
    function redeem(RedeemInfo redeemInfo) external;
}

documentation.text:
@notice Transfer some KDAOs to a given address.
If the `to` address is `PROTOCOL_FUND`, the transfer is understood as a
redemption: the sent KDAOs are burned and the portion of `PROTOCOL_FUND`
corresponding to the sent KDAOs are given back to the `msg.sender`.
Sending to the 0 address is disallowed to prevent user error. Sending to
this contract and the `KilitliKDAO` contract are disallowed to maintain
our invariants.
@param to               the address of the recipient.
@param amount           amount of KDAOs * 1e6.

